    startupWith: function(ctx,args){
      args = args || []
      args = [ctx,args]
      fire(args)
    }
    startup: function(this){
      self.startupWith(this,arguments)
    }
    has: function(fn){
      return stack.indexOf(fn) > -1
    }
  }
  return self
}
function createFlags(flags){
  var res = {}
  (flags.match(/\S+/g) || []).forEach(function(flag){
    res[flag] = true
  })
}

