class Dep{
  constructor(){
    let deps = new Set()
  }
  // 收集依赖，将依赖加入「依赖集」
  depend(){
    if(active) this.deps.add(active)
  }
  // 通知依赖
  notify(){
    // 这些依赖并不直接直接执行
    // 而是先加入异步队列 然后异步执行
        this.deps.forEach((dep)=>queueJob(dep))
    nextTick(flushJob) 
  }
}
